SELECT
    DAYNAME(CONCAT(YEAR(NOW()), '-', SUBSTRING(birthday_at, 6, 10))) AS week_day,
    COUNT(*) AS birthday
FROM users
GROUP BY week_day
ORDER BY birthday ;


